<section class="component component-contact">

   <style>
      .contact .main-inner {
         display:none;
      }

      .component-contact {
         margin: 0;
         width: 100%;
         max-width: 100%;
         background: none;
         padding: 0;
      }

      .component-contact .l-col-6 {
         width: 50%;
         max-width: 50%;
         flex: 0 0 50%;
         margin: 0;
      }

      .component-contact > h2 {
         font-size: 42px;
         margin-bottom: 20px;
         text-align: center;
      }

      .component-contact .intro {
         font-size: 18px;
         text-align: center;
      }

      .component-contact .inner {
         max-width: 85vw;
         margin: 0 auto;
      }

      .component-contact-container {
         margin: 0;
         padding: 0 1em;
         width: 100%;
         max-width: 100%;
         border-radius: 0;
         background-color: #f0f5ff;
      }
         
      .component-contact .col-one {
         display: flex;
      }

      .component-contact .content-section {
         margin-bottom: 2em;
      }

      .component-contact .content-inner {
         position: relative;
         width: 90%;
         padding: 1em;
         border-radius: 0;
         display: flex;
         flex-direction: column;
         justify-content: center;
         align-items: center;
         top: -21px;
      }

      .component-contact .content-inner svg {
         max-width: 620px;
         height: auto;
            margin-top: 0;
         margin-bottom: -1em;
      }

      .component-contact .content-inner h2 {
         font-size: 3em;
         margin-bottom: 10px;
      }

      .col-two {
         padding: 3em 0;
      }

      .component-contact .btn {
         margin-top: 0;
      }

      .component-contact.component-faq-secondary {
         margin-top: 5em;
      }

      .component-contact .content-inner .fa-long-arrow-alt-right {
         margin: 0;
         height: auto;
         width: 40px;
         position: relative;
         top: -56px;
         left: -4px;
      }

      @media (max-width: 900px) {
         
         .component-contact-container {
            padding: 0;
         }

         .component-contact .inner {
            flex-direction: column;
            margin: 0;
            max-width: 100%;
         }

         .component-contact .col-one,
         .component-contact .l-col-6 {
            display: flex;
            width: 100%;
            max-width: 100%;
            flex: 0 0 100%;
         }

         .component-contact .content-inner {
            width: 100%;
         }

         .component-contact .l-col-6 {
            display: block;
         }

         .component-contact .content-inner svg {
               max-width: 380px;
         }

         .component-contact .content-inner h2 {
               font-size: 42px;
         }

         .component-contact .content-inner .fa-long-arrow-alt-right {
            display: none;
         }

         .component-contact .content-section {
            margin-bottom: 2em;
            max-width: 500px;
            margin: 0 auto 3em auto;
         }

         .component-contact .l-col-6.col-two {
            padding-bottom: 0;
            padding-left: 15px;
            padding-right: 15px;
            padding-top: 0;
         }

      }
   </style>
